const meta: Meta<typeof Modals> = {
  title: 'components/Modals',
  component: Modals,
  decorators: [withTests({results})],
};

export default meta;

const ExampleComponent = props => {
  const [show, setShow] = useState(false);

  return (
    <>
      <Button
        label="Open Modal"
        variant="filled"
        type="primary"
        onPress={() => {
          setShow(true);
        }}
      />

      <Modals
        {...props}
        footer={props.footer}
        header={props.header}
        coverScreen={false}
        isVisible={show}
        onClose={() => setShow(false)}>
        {props.children}
      </Modals>
    </>
  );
};

const Template = args => <ExampleComponent {...args} />;

